    // Start is called before the first frame update
    void Start()
    {
        explanations = new List<Bilgiler>();
        explanations.Add(AciklamaConventer(1, "BALABAN", "Nefesli", "Balaban, Mey veya Duduk, Güney Kafkasya ve Orta Asya ülkelerinin birçoðunda kullanýlan nefesli çalgýlar grubuna ait bir çalgýdýr. 9-10 adet ses perdeli olanlarý Azerbaycan ve Türkistan’da kullanýlmaktadýr. Gürgen, ceviz, erik ve benzeri sert aðaçlardan yapýlanlarý varsa da en makbulü kayýsý aðacýndan yapýlanýdýr. Bir gövde(govda) ve aðýz tarafýna takýlan ses çýkarýcý yassý kamýþ aðýzlýktan ibaret olan Balaban, 280 - 300 milimetre uzunluðunda ve 20 - 22 milimetre çapýndadýr. Sesi mat ve hafiftir, sesi zayýf olduðu için daha çok kapalý mekanlarda ve oda toplantýlarýnda çalýnmaktadýr.Kamýþ üzerindeki kýskaç sayesinde ses inceltilip kalýnlaþtýrýlabilmektedir.Özel bir yöntemle yassýlaþtýrýlan kamýþ, kýskaç ve gövdeden meydana gelir.Kýskacýn kamýþ üzerinde aþaðý veya yukarý doðru itilmesiyle yaklaþýk bir perdelik ses deðiþimi yapýlabilmekte ve bu özelliði ile çalgý gruplarýna hemen uyum saðlayabilmektedir."));
        explanations.Add(AciklamaConventer(2, "BAÐLAMA", "Telli", "Baðlama ya da saz, Türk halk müziðinde yaygýn olarak kullanýlan telli tezeneli bir çalgý türüdür. Yörelere ve boyutlarýna göre kopuz, cura, saz, çöðür, dombra, ikitelli, tambura, tar gibi deðiþik isimlerle tanýnýr. Tezene denilen kiraz aðacý kabuðundan yapýlmýþ bir mýzrapla tellere vurularak çalýnýr. Tekne bir kütükten oyulmuþtur ve armut biçimindedir. Kullanýlan tekniðe göre mýzrap(tezene) veya parmaklar ile çalýnýr. Parmaklarla çalma tekniðine þelpe ve dövme denir.Genellikle altta iki çelik ile bir sýrma bam, ortada iki çelik ve üstte bir çelik ile bir sýrma bam teli olmak üzere toplam 7 tellidir.Tezene ile çalýnýr."));
        explanations.Add(AciklamaConventer(3, "KEMENÇE", "Telli", "Kemençe, rebap, keman türü yaylý çalgýlarla akraba olduðu sanýlan, bir yay yardýmýyla çalýnan üç telli geleneksel halk çalgýsýnýn adý olup, klasik kemençe ile karýþtýrýlmasýný önlemek amacýyla Karadeniz kemençesi olarak da adlandýrýlmaktadýr. Bilinen en eski yaylý enstruman olan rebap(Arapça rababah) Avrupaya, 9.yüzyýlda Bizans üzerinden(lyra adýyla) ve MS 11.yüzyýlda Müslüman Araplarýn kontrolü altýnda olduðu dönemde Ýspanya üzerinden Rebec adýyla iki koldan yayýlmýþ, Orta Çað ve Erken Rönesans dönemi boyunca yoðun olarak kullanýlmýþtýr. Çok sayýda farklý teoriye karþýn Karadeniz kemençesinin Rumlar tarafýndan Kapadokya kemanesi olarak da adlandýrýlan Kabak kemaneden form olarak ne zaman farklýlaþtýðý kesin olarak bilinmemektedir. Araþtýrmacý Yazar Mehmet Bilginin Doðu Karadeniz Etnik Tarihi Üzerine adlý yazdýðý kitapta, Karadeniz kemençesinin Kýpçak Türklerine ait bir çalgý olduðunu, Gagavuz Türklerinin de bu çalgýya kumança ve oyununun adýna da Horon dediklerini yazmýþtýr."));
        explanations.Add(AciklamaConventer(4, "NEY", "Nefesli", "Ney, üflemeli çalgýdýr. Kaþgarlý Mahmut, Divân-ý Lügatit-Türk adlý Türk kültür ve dilini anlatan eserinde, Sagu denilen, Erler için düzenlenen, ölüm, erdem ve acýlarý anlatan törenlerde kullanýldýðýný aktarmýþtýr. Ney, yakýn zamanlarda Farsçaya geçmiþ olup nâ veya nay (kamýþ) adýný almýþtýr. Arap toplumunda da üflemeli çalgýlarýn hemen tümü için kullanýlan mizmâr sözcüðü ise, (nefes borusu, ses organý anlamýnda) ney için de kullanýlmýþtýr. Türkçede ise hemen her zaman ney olarak anýlmýþtýr. Kavimler Göçünden çok eski zamanlardan kalan, Runik Harflerin aslýnýn henüz anlaþýldýðý; Proto Türk Yazýtlarý zamanýndan kaldýðý düþünülen kültürizleri gibi miras kalmýþ olan, çok az kültürel öðelerin devamý olarak ise, bugünkü Romanya’da nayu olarak adlandýrýlýr. Sümer toplumunda MÖ 5000 yýllarýndan itibaren kullanýldýðý sanýlan bu çalgýya ait elimizdeki en eski bulgu, MÖ 3000 - 2800 yýllarýndan kalan bugün Amerikada Philadelphia Üniversitesi Müzesinde sergilenen neydir.Çalgýnýn o dönemlerde de dinsel törenlerde kullanýldýðý sanýlmaktadýr. Günümüzde ney, Türk sazý olarak anýlmaktadýr ve tasavvuf müziðinin bir simgesi haline gelmiþtir."));
        explanations.Add(AciklamaConventer(5, "TAR", "Telli", "Tar, uzun saplý; Ýran, Azerbaycan, Gürcistan, Ermenistan ve kýsmen Türkiyede kullanýlan telli bir çalgýdýr. Tar kelimesi, Farsçada tel anlamýna gelir. Tar çalan kiþiye ise tarzen veya tarist denmektedir. Bu çalgýnýn iki buçuk oktav ses sahasý bulunmaktadýr. 8 yýl önce UNESCO tarafýndan Dünya Miras Listesinde alýnmýþtýr.[1] Günümüzde Ýranlýlar ve Azerbaycanlýlar bu çalgýnýn kendi kültürlerine ait olduðunu iddia ederler. Bu durumun yaný sýra iki ülkede de tar farklý þekillerde kullanýlmaktadýr.Kopuzdan gelen sazlardandýr. Tar; setar, dutar, pençtar ve gitar gibi enstrümanlarýn atasýdýr. Bu çalgýnýn ne zaman ve nerede ilk olarak kullanýldýðýna dair elimizde pek bilgi olmamasýna raðmen 10. yüzyýldan kalma birkaç þiirde tar isminin geçtiði bulunmuþtur. "));
        explanations.Add(AciklamaConventer(6, "UD", "Telli", "Ut ya da Ud, telli bir müzik aleti. Kelimenin aslý Arapçada sarýsabýr veya ödaðacý anlamýndaki el-oud dan gelir. Ud çalan kiþiye udî denir. Kimi kaynaklarda udu Farabi’nin icat ettiði söylenir ancak Farabi’den çok önce minyatür ve kabartmalarda ud ve benzeri çalgýlar bulunur.Farabi’nin mucit olarak algýlanmasýnýn esas sebebi uda hakim bir müzisyen olmasý ve uda getirdiði akort sistemidir. Döneminde ud hakkýnda en kapsamlý bilgiyi verenlerden biri olan Farabi, o döneme kadar 4 telli bir saz olan uda 5.teli eklemiþtir.[1] Ud hakk?nda Farabi’den Ýbni Sina(980 / 1037) Kitabuþ Þifa adlý eserinde en meþhur aletlerden biri olduðunu söyler. Akordu ve ses aralýklarý gibi teknik bilgileri, þekillerle anlatýr. 10. yüzyýlda Ýhvan-ý Safa risalelerinde musiki aletlerinden bahsedilirken bunlarýn en güzel olanýnýn ud olduðu belirtilir. "));
        explanations.Add(AciklamaConventer(7, "ELEKTRO GÝTAR", "Telli", "Elektro gitar veya elektronik gitar, sesini manyetikleri aracýlýðý ile elektrik akýmýna dönüþtüren ve bir amplifikatör ile bu akýmdan ses elde edilmesine olanak tanýyan gitar türüdür. Oluþturduðu sinyalin deðiþtirilebilir olmasý, ve zamanýnda bir devrim niteliði taþýyan yükseklikte bir sese sahip olmasý nedeniyle, kullaným alaný çok geniþlemiþ bir gitar türüdür. Elektronikteki geliþmeler ile tonal olarak sýnýrlarý 1960lý yýllardan özellikleri ve çeþidi artan elektro gitarlar, özellikle 1980li yýllarýn baþlarýndan itibaren üretilen süperstrat modeller baþta olmak üzere günümüzün en bilindik enstrümanlardan biri haline gelmiþtir. "));
        explanations.Add(AciklamaConventer(8, "GÝTAR", "Telli", "Gitar, parmakla veya pena ile çalýnan, esasen sekiz þekline benzeyen, yan kýsýmlarý oval, sap üzerinde ses perdeleri olan, telli bir çalgý türü. Gitarlar genelde altý tellidir ve farklý çeþitlerdeki aðaç türlerinden yapýlabilirler. Gitar neredeyse her türlü müzik türünde kullanýlan bir müzik aletidir."));
        explanations.Add(AciklamaConventer(9, "CÜMBÜÞ", "Telli", "Cümbüþ, Zeynel Abidin Cümbüþ tarafýndan 20. yüzyýl baþlarýnda geliþtirilmiþ olan[1] telli bir müzik aletidir. Sap kýsmý gövde ile baðlandýðý noktadan ayrýlabilmektedir. Bu þekilde yalnýz telleri deðiþtirilerek mandolin, gitar gibi birçok enstrümana çevrilebilir. Genel yapýsý banjoya ve uda benzer. Gövdesinin bazý kýsýmlarý alüminyumdan yapýlýr. Göðüs tahtasý deriden bir sazla kaplýdýr. Diðer telli sazlar gibi mýzrapla çalýnýr Teknesi yuvarlaktýr ve metaldendir. 12 teli vardýr(yani altý çift tel). Sapý perdesizdir. Teller çeliktendir. "));
        explanations.Add(AciklamaConventer(10, "MANDOLÝN", "Telli", "Mandolin, uta benzeyen telli çalgýdýr. Mýzrapla çalýndýðý zaman iyi ses verdiði bilinir ancak modern mandolinlerde genelde pena kullanýlmaktadýr. Mýzrap ile çalýnabilmesi mandolini ut ve gitardan ayýrýr. Dört çift teli olan mandolinin ses düzeni kemandaki gibidir. Kolay öðrenilir olmasý nedeniyle müzikle yeni tanýþanlarýn tercih ettiði bir müzik aletidir. Mandolin ut ailesinin modern üyelerinden birisidir. Mandore, pandurina, ut, terbo, arþilut ve gitar, mandolinin yakýn akrabalarýndandýr.Bunlarýn içerisinde günümüzde en yaygýn olaný gitardýr. "));
        explanations.Add(AciklamaConventer(11, "ARP", "Telli", "Arp, çoðunlukla Klasik Batý Müziðinde kullanýlan en eski, telli bir müzik aletidir. Sabit sesli bir çalgýdýr. Her tel üç ayrý ses çýkarabilir. Gam notalarýnýn karþýlýðý olan yedi pedalýn tutturulduðu oluk hazinesi, çýnlama kasasý sütun ve konsol bölümlerinden oluþmaktadýr. Bir yayla ok atýlýrken gerilip býrakýlan yay kiriþi bir týnlama sesi çýkarýr. Tek telli olan yaya deðiþik uzunluklarda teller ekleyip onlarýn gerginlikleriyle oynayarak deðiþik sesler çýkarýldý. Sonralarý sesi yükseltmeye elveriþli bir gövde(týný kutusu) eklenince gerçek bir çalgý yapýlmýþ oldu. "));
        explanations.Add(AciklamaConventer(12, "KEMAN", "Telli", "Keman (veya viyolon), viyola ve viyolonselin de bulunduðu violin ailesinin en yüksek tondan çalan, en küçük üyesidir. Dört teli vardýr. Akort sesleri pesten tize sol, re, la ve midir. Keman yayýna arþe denir. Arþede yapay ya da gerçek at kýlýnýn yaný sýra ham misina olarak adlandýrýlan bir madde de kullanýlabilir.Ayrýca yayýný düzgünleþtirmek ve sesi güzelleþtirmek amacýyla arþeye reçine adý verilen madde sürülür.Reçine bala benzeyen sarý renkli katý bir maddedir. Almanca Geige, Fransýzca violon, Ýngilizce violin, Ýtalyanca violinodur. Uzunluðu 60 cmdir.Yayla çalýnan telli bir çalgýdýr.Notasý, ikinci çizgi Sol açkýsý ile yazýlýr. Orkestralarda, genel olarak(Solo, I, II) üç partisi bulunur.Solo ve eþlik görevi verilir(özellikle bir solo çalgýdýr).Hiçbir çalgýda olmayan ses rengiyle, çok zengin bir anlatým gücü vardýr. "));
        explanations.Add(AciklamaConventer(13, "PÝYANO", "Tuþlu", "Piyano, tuþlu bir çalgýdýr. Piyanoda ses, teller vasýtasýyla elde edilir. Piyanonun tuþlarýna basýldýðýnda içindeki tahta çekiç tellere vurarak sesi oluþturur. Tahta çekicin tellere vurmasýndan dolayý piyano bazen vurmalý telli çalgý olarak da sýnýflandýrýlýr. Piyano klasik ve caz müzikte yaygýn olarak kullanýlýr. Solo performanslar, ansambl, oda müziði, eþlik, bestecilik ve prova için oldukça uygun bir enstrümandýr. Piyano taþýnabilir bir enstrüman olmamasýna ve genelde pahalý olmasýna raðmen çok yönlülüðü ve ayný anda birçok yerde bulunma özelliði ile dünyanýn en yaygýn olarak kullanýlan enstrümanlarýndan biridir. Akustik piyanolar genellikle ses tahtasýný ve metal telleri çevreleyen ayný zamanda koruyan ahþap kasadan oluþmakla birlikte 88 tuþa(52 beyaz tuþ, 36 siyah tuþ) sahiptir.Piyano, tuþlarýna basýldýðýnda içerisindeki teller aracýlýðýyla ses çýkarýr, tuþ býrakýldýðýnda ise teller damper(titreþim azaltan parça) yoluyla susturulur. Fakat pedallar yardýmýyla tuþlardan parmaklar kaldýrmasýna raðmen sesi uzatmak mümkündür. Piyanoda herhangi bir tuþa bastýðýmýzda keçe ile kaplanmýþ çekiç o tuþa ait tellere vurur, ardýndan geri gelir ve çekiç eski konumuna gelmesine raðmen teller titreþmeye devam eder. "));
        explanations.Add(AciklamaConventer(14, "AKORDEON", "Havalý", "Akordeon, akordiyon ya da akordion, bir körüðü harekete geçirmekle yaratýlan hava akýmýnýn etkilediði serbest metal dillerinin titreþmesiyle ses çýkaran havalý çalgýdýr. Günümüz Türkiye Gürcülerinin bulunduðu Artvin ve Ardahan bölgelerinde yaygýn olarak kullanýlmaktadýr. Bir ya da iki kýlavuz ile bir körükten oluþan akordiyonda, serbest metal dillerin titreþmesi, klavyenin tuþlarýna basmakla saðlanýr. Akordeonun ilkel þeklinin 1822de Berlinde Christian Friedrich Ludwig Buschmann tarafýndan icat edildiðine inanýlýr. Ama yakýn zamanda akordeon olarak adlandýrýlabilecek bir enstrümanýn 1816da veya daha önceki bir tarihte Nürnbergli Friedrich Lohner tarafýndan kullanýldýðý saptanmýþtýr. Akordeon ismine ilk patent ise 1839da, Viyanalý org ve piyano yapýmcýsý Cyrillus Demian tarafýndan günümüzdeki akerdeona çok da benzemeyen tek klavyeli küçük bir çalgý alýndý. "));
        explanations.Add(AciklamaConventer(15, "UKULELE", "Telli", "Ukulele Hawaii dilinde zýplayan pire anlamýna gelen telli çalgýlar ailesine ait bir enstrüman. Dört telli bir çalgý olan ukulele çoðunlukla uke olarak kýsaltýlýr. Teller G, C, E, A veya A, D, F#, B þeklinde akorlanabilir ve genellikle naylon veya kiriþten yapýlmaktadýr. Ukuleler kendi aralarýnda dört boyuta ayrýlýrlar, bunlar: soprano, konser, tenor ve baritondur. Ukulele Hawaii müziði ile baðdaþlaþtýrýlmasýna raðmen Hawaii yerlilerinin aksine 19.yüzyýlda Hawaiiye Portekizin Madeira adalarýndan gelen Portekizli göçmenler tarafýndan geliþtirilmiþtir.[2] Portekizde geliþtirilmiþ ukulele benzeri cavaquinho ve rajão adlý enstrümanlarýn ukulelenin atasý olduðu düþünülmektedir. "));
        explanations.Add(AciklamaConventer(16, "DOMBRA", "Telli", "Dombra, uzun boyunlu bir yaylý Türk çalgýsýdýr. Kazakistan, Özbekistan, Kýrgýzistan gibi Orta Asya ülkelerinde yaygýn olarak kullanýlmaktadýr. Enstrüman ülkelere ve bölgelere göre farklýlýklar gösterebilir.Kazak dombrasýnýn perdeleri vardýr. Teller geleneksel olarak hayvan sinirlerinden üretilirken, modern dombralar genellikle naylon teller kullanýlarak üretilir. En büyük dombra sanatçýlarýndan birisi olan Kurmangazi Saðýrbayoðlunun Kazak müzik kültüründeki etkisi büyüktür.  Türkistan ve Bedahþan dombralarý genellikle dut veya kayýsý olmak üzere tek bir tahta bloktan oyulmuþ bir gövde ile yapýlýr. Dombra ilk olarak Orta Çaðda ortaya çýkmýþtýr. Türklerin ortak müzik aleti olarak kabul edilir. Nogay Türklerinin kültüründe de önemli bir yer tutar.Dombranýn Kazakistan kültüründeki öneminden dolayý 2018den itibaren resmi olarak Millî Dombra Günü kutlanmaya baþlandý.[4] Millî Dombra Günü temmuz ayýnýn ilk pazar günü kutlanmaktadýr. "));
        explanations.Add(AciklamaConventer(17, "BENDÝR", "Vurmalý", "Bendir, Klasik Türk müziðinde kullanýlan baþlýca vurmalý ritim çalgýlarýndan biridir. Zilsiz büyük def, nakkare ve kudümle birlikte kullanýlýr.Derisinin iç yüzüne boydan boya gerilen kiriþ sayesinde ayný anda iki deðiþik týný çýkarabilir. Bendir, Klasik Türk müziðinde ve özellikle Mevlevi Türk Tasavvuf Musikisinde daire veya def adýyla bilinen vurmalý çalgýnýn Maðrip ülkelerine(özellikle Fas ve Cezayire) özgü biçimidir. Maðrip Arapçasýndan alýnan bendir adý Türkiyede 1980lerden sonra yaygýn kullanýma kavuþmuþtur. Türkiyede son dönemlerde kullaným alaný yaygýnlaþmaya baþlamýþ olup, Türk Tasavvuf Müziðinin yanýnda Türk Halk Müziðinde en çok kullanýlan ritim aleti olmuþtur. "));
        explanations.Add(AciklamaConventer(18, "DARBUKA", "Vurmalý", "Darbuka veya dümbelek, özellikle Orta Doðuda ve Balkanlarda kullanýlan vurmalý bir çalgýdýr. Darbukada düm ve tek olarak adlandýrýlan iki ses vardýr: Ýlk ses, ritmin temelini saðlar ve aletin ortasýna vurularak saðlanýr, ikinci ses daha çok süsleme ve doðaçlama için kullanýlýr.Darbuka ayný zamanda düðün niþan sünnet düðünlerinde kullanýlan bir tür vurmalý çalgýdýr. "));
        explanations.Add(AciklamaConventer(19, "DEF", "Vurmalý", "Def veya tef. Yuvarlak bir tahta kasnaðýn bir veya iki yanýna deriden bir örtü geçirilerek yapýlan ve parmak vuruþlarýyla çalýnan müzik aleti. Def, Türk Musikisinde bir usul vurma aletidir. Bir çeþit açýk davul olan tefin çeþitli þekillerine eski kavimlerde de rastlanýr. Araplardan Ýspanya yoluyla Avrupaya geçmiþ ve adýna tambour de basque denmiþtir. Orkestraya da girmiþtir.Türk musikisinde, fasýl þefi olan ser hanendenin veya birkaç hanendenin elinde bulunur.Parmak vuruþlarýyla usul tutulur. Her vuruþta, kasnaktaki ince pirinçten 8 çift küçük zil daireye çarparak týnlar "));
        explanations.Add(AciklamaConventer(20, "KANUN", "Telli", "Kanun, Orta Doðu ve Kuzey Afrikada yaygýn olarak kullanýlan bir telli çalgýdýr. Ýsmi Arapça yasa ve yönetmek manasýndaki kanun kelimesinden gelmektedir. Araplar ise kelimeyi Greklerin ????? (kan?n) kelimesinden ödünç almýþlardýr. Kanun çalgýsýnýn bilinen tarihi MÖ. 19.yya dayanmaktadýr. Bilinen en eski kanun Süryanilerin Nimrud adlý þehrinde bulunmuþtur. 24 veya 27 perdeli bir sazdýr.Her bir perdedeki sesi 3 tel týnlatýr. Bu yapýsýyla klavsenin ses sistemine benzer. Telleri özel olarak müzik aletleri için üretilmiþ naylon teldendir.Ýlk evrelerinde naylon tel yerine baðýrsaktan yapýlan kiriþ teller kullanýlmýþtýr. "));
        explanations.Add(AciklamaConventer(21, "KAVAL", "Nefesli", "Kaval, çoban çalgýsý olarak bilinen kaval, yörede daha çok þimþir aðacýndan (nadiren livori, incir ve erik aðacýndan), altta 1 ve üstte 7 delikli olarak imal edilir. Dilli kaval ve dilsiz kaval olarak adlandýrýlan iki türü vardýr. Dilli kavalýn ucunda ses üretimini saðlayan bir düdük bulunur. Dilsiz kaval ise içi boþ bir boru olup çalan kiþi nefes teknikleriyle istenen sesi çýkarýr. Çobanlarýn kavalý üflemesindeki bir amaç, otlanan koyun keçi gibi hayvanlarýn sakin kalmasý ve sürüden ayrýlmamasýdýr, bu gelenek hâlen günümüzde de uygulanmaktadýr. Kavalýn kökeni hakkýnda deðiþik görüþler varsa da insanoðlunun en eski çalgýlarýndan olduðu bilinmektedir.Ortadoðu ve orta asyada deðiþik formlarýna rastlanýr. "));
        explanations.Add(AciklamaConventer(22, "KOPUZ", "Telli", "Kopuz – Türk ve Altay halk kültüründe çalgý. Komus da denir. Baðlamanýn atasý olan müzik aletidir. Türkler’de önemi büyüktür. Baðlama ve kopuz kutsal sayýlýr. Bu kelimeyle iliþkili olan kobzamak, kopzatmak gibi fiiller çalgý çalmayý ifade eder. Eski Türk kültüründe önemli yeri vardýr. Havada uçan veya ölen kopuzlar masallarda zaman zaman yer alýr. Kopuzun, Korkut Ata’nýn buluþu olduðu söylenir. Hastalýklarýn tedavisinde bile kullanýlýr. Kopuzun sahibi onu bir baþkasýna vermeyi tabu sayar ve birinin elinin deðmesinin günah olduðunu kabul eder. Þamanlar ruhlarý onunla çaðýrýr. Aldacý(Ölüm Tanrýsý) bile Kopuz’un sesinden Korkut Ata’nýn canýný veya o civarda bulunanlarýn ruhunu almaya gelemez.Onun sesinde bir haþmet vardýr.Korkut Ata öldükten sonra kopuzu yýllarca acýklý sesler çýkarmýþtýr. Kopuz, en eski Türk halk çalgýsý olarak bilinmektedir.Ancak kopuz adýyla anýlan farklý çalgýlara rastlanmaktadýr.Þu anda hâlâ Anadolu, Kafkasya ve Orta Asyada kullanýlmaktadýr. Þamanlar törenlerde kopuzu kullanýrlardý. Teknik olarak çalgý baðlama ailesi içinde deðerlendirilmelidir. Bunun yanýnda diðer önemli telli çalgýlar arasýnda Iklýð ve Gizek adý verilen yaylý olanlar ile Yatuðan denen ve yatýk olarak çalýnanlar yer alýr. "));
        explanations.Add(AciklamaConventer(23, "TAMBUR", "Telli", "Tambur, Türk müziðinde yaygýn olarak kullanýlan telli bir sazdýr. Tamburun kökeni, hangi tarihte ortaya çýktýðý bilinmemektedir. Sümerce pantur dan geldiði hakkýnda bilgiler mevcuttur.Araplar, kelimenin kuzunun kuyruðu anlamýna gelen dumba-i bara dan geldiðini söylerler.Sözcük, sonralarý Ýranda ve Orta Asyada, daha çok baðlamaya benzeyen armudi gövdeli, uzun saplý çalgýlarýn adý olarak kullanýlmýþtýr.Özellikle Avrupalý gezginlerin(örn.Charles Fonton ve Toderini), sapýndaki perde baðlarý dolayýsýyla Türk müziðinin ses sistemini gözle görülür biçimde yansýttýðýný yazdýklarý tambur, günümüzde yalnýzca Türkiyede kullanýlan belki de tek çalgýdýr. Sazý icra edenlere tamburi ismi verilir. Farabi, horasan tamburu ndan bahsetmektedir. Evliya Çelebi XVII. yüzyýlda Ýstanbulda 500 tamburi bulunduðunu ifade ediyor. Tarihte tambura; Farabide Horasan tanburu, Maragali Abdulkadirde tanbur-ý Þirvaniyan ile tanbura-i Türki adlarýndaki çeþitleri ile deðinilmiþtir. "));
        explanations.Add(AciklamaConventer(24, "TULUM", "Nefesli", "Tulum, Anadolunun kuzeydoðusunda Rize, Artvin ve Gümüþhanede[1] kullanýlan nefesli bir halk çalgýsý. Balkan yarýmadasý ve Ýskoçyada kullanýlan gaydadan en önemli farklý pes sesleri kontrol edebilen boruya sahip olmamasýdýr. Tulumun bir enstrüman olarak kullanýlmaya baþlandýðý tarih hakkýnda kesin bir bilgi olmamakla birlikte Mezopotamyada ortaya çýktýðý sanýlmaktadýr. Tüyleri temizlenmiþ çebiç adý verilen oðlak derisinden delik yerleri baðlanýp, gövde bölümü elde edilir.Ön ayaklardan birine lülük, birine de nav takýlarak yapýlmaktadýr. "));
        explanations.Add(AciklamaConventer(25, "ZURNA", "Nefesli", "Zurna, Türkiyenin birçok yerinde kullanýlýr. Tahta, metal ve kamýþ kullanýlarak yapýlan, yüksek sesli, bu yüzden büyük davul ile birlikte çalýnan, yine bu yüzden açýk havada kullanýma uygun, nefesli saz çeþididir. Güneydoðu Anadolu Bölgesinin folklorunda genellikle(Mardinde Gaziantep, Diyarbakýr ve Þanlýurfanýn iç bölgelerinde) ancak mutlaka bas davul eþliðinde kullanýlan, ahþap, yedi delikli nefesli bir sazdýr. Mehter takýmlarý ve bunun modern þekli bando mýzýka takýmlarýnýn eskiden hükümdarlarýn hükümdarlýk iþareti olarak fermanlarýný zurna eþliðinde okutmalarýndan geliþtiði tahmin edilmektedir. Türkiyede olduðu gibi Fastan Çine kadar uzanan iklim kuþaðýndaki her ülkede kullanýldýðý da bilinmektedir. Ayrý-ayrý türleri Orta Doðu ve Kafkasya halklarý arasýnda çok geniþ yayýlmýþtýr. "));
        explanations.Add(AciklamaConventer(26, "DAVUL", "Vurmalý", "Davul, bilinen en eski vurmalý çalgýlardan biridir. Ahþap, maden ya da piþmiþ topraktan silindirik bir gövdeye gerilen deriden oluþur. El ya da sopayla çalýnýr. Biçimi deðiþse de dünyanýn her yerinde ve her toplumda kullanýlan bir çalgýdýr. Türkçede davulun diðer adlarý; köbürge, küvgür, tuð, tavul, tabýl(veya babl)dýr.Davul çalanlara davulcu, tabilzen, tabbal gibi adlar verilirdi. 8.yüzyýlda köbürge, daha sonralarý tuð ve 11.yüzyýlda küvrüð adýný almýþtýr.Bandolarda kullanýlan ve baget ile çalýnan küçük davullara ise trampet denir. Davul, en yaygýn vurmalý çalgýlardan biridir ve dünyadaki hemen bütün halklarýn kültürlerinde yeri vardýr.Yazýlý tarihten çok önce Eski Mýsýrlýlarýn, Asurlularýn ve Uzakdoðulularýn davulu kullandýklarý bilinmektedir.Bilinen en eski daalahelaný versinvulun neolitik çaðda yapýlmýþ olmasýna karþýn, insanýn ritim duygusunun çok daha önce geliþmiþ olduðu sanýlmaktadýr. Davul, en basit çalgýlardan biridir ve iki temel parçadan oluþur.Bunlardan biri boru ya da silindiri andýran kasnaktýr. Kasnak tahta ya da metal olabilir. Ýkincisi bu kasnak gövdenin bir ya da iki yüzüne gerilerek geçirilen ince dana derisi ya da benzeri esnek bir malzemedir. Gerilen bu malzemeye davul derisi denir.Davul, derisine elle ya da sopayla vurularak çalýnýr.Bu sopaya davul tokmaðý da denir. "));
        explanations.Add(AciklamaConventer(27, "BATERÝ", "Vurmalý", "Bateri, davullar ve ziller baþta olmak üzere vurmalý çalgýlardan oluþan bir müzik enstrümaný. Þarkýlarda ritim tutmaya yarar. Fransýzcadan Türkçeye geçmiþ bir kelimedir. Türkçede bateri yerine davul, davul seti ve davul kiti terimleri de kullanýlýr. Bateri çalma, hýzýn yaný sýra denge ve akýcýlýk gerektirir. Düzenli olarak metronom ile çalýþmalý ve single - stroke, double-stroke, paradidle, double paradidle, üçlemeler, altýlamalar gibi el teknikleri, dörtlük ritmin üzerine dörtlük, sekizlik, on altýlýk kick vuruþlarý gibi sað ayak teknikleri(eðer twin pedal tercih edilirse sol ayak için de ayný egzersizler uygulanabilir), hi - hat ile dörtlük, sekizlik, on altýlýk saymak gibi sol ayak teknikleri ve tabii ki el-ayak kombinasyonlarý gibi teknikler üzerinde alýþtýrma yapýlmalýdýr. Standart bir bateri, genellikle 5 parça davul ve 3 parça zilden oluþur.Davullar tom 1(high tom), tom 2(mid tom), floor tom(low tom), trampet(snare) ve pes davuldan(cross, kick, bass drum) ve 3lü zil takýmýndan (crash, hi-hat, ride) oluþur. Bu standartlarýn dýþýnda davula mid tomun yüzey alanýna oranlý (örneðin 10luk ise 8, 12 ise 10luk) bir mid tom, ayrýca floor tomun yüzeyine oranlý(örneðin 14 ise 16, 16 ise 18lik) bir floor tom daha eklenebilir.Davul kitleri tamamen kiþinin isteðiyle düzenlenebilir. Darbuka dahi ekleyen davulcular mevcuttur.Bununla beraber, zil seçenekleri de yeni crashler, splashler, chinalar, raw ve cow ziller, sentez ziller olan ride-crash, splash - china, splash - crash vb.eklentilerle çoðaltýlabilir. "));
        explanations.Add(AciklamaConventer(28, "ZÝL", "Vurmalý", "Zil, Türk müziðinde ve diðer birçok Orta Doðu ülkesinin müzik kültüründe var olan bir usul vurma çalgýsýdýr. Yuvarlak biçimlidir ve genellikle pirinçten yapýlýr, ama imalatýnda baþka alaþýmlar da kullanýlabilmektedir. Ortasýnda deriden ya da ipten yapýlmýþ tutamaklar bulunur. Bu tutamaklarla iki zil birbirine çarpýlarak çalýnmaktadýr. Oryantal müzik icrasýnda kullanýlan küçük zillerin çapý 5 cm civarýndadýr. Ayrýca mehter müziðinde kullanýlan çeþitli boylarda ziller de vardýr."));
        explanations.Add(AciklamaConventer(29, "KLARNET", "Nefesli", "Klarnet (klarinet ya da gýrnata), sert ve dayanýklý aðaçlardan genellikle de abanoz aðacýndan yapýlan üflemeli bir çalgý türüdür. Bir çeþit sert kauçuk olan ebonitten, ayrýca metalden yapýlanlarý da vardýr. Klarnetler, beþ parçanýn birleþmesinden oluþur, bunlar: Aðýzlýk(Bek), Fýçý(Barel), Üst gövde, Alt gövde, Kalak(Pavillon da denir) Klarnetin gövdesi silindir biçimindedir. Kalak bölümü ise obuanýn kalaðýna oranla daha geniþtir. Dikkatlice yontulup biçimlendirilen bu kamýþ parçasý, aðýzlýk üzerine takýlýr. Çalýcýnýn nefesi ile titreþime geçirilen kamýþ, boru içindeki havayý titreþime geçirerek ses elde edilmesini saðlar. Çalýcýnýn sol eli yukarýda, sað eli ise aþaðýda olmak üzere az bir eðimle yere doðru tutulur. "));
        explanations.Add(AciklamaConventer(30, "SAKSOFON", "Nefesli", "Saksofon veya saksafon, çoðunlukla koni ve “S” biçiminde pirinçten üretilen, aðzýndaki kamýþ vasýtasýyla ses çýkaran bir çalgýdýr. 1840’lý yýllarda, Adolphe Sax tarafýndan tasarlanmýþtýr. Saksofon genellikle pop ve caz müziði ile iliþkilendirilse de, önceleri klasik batý müziði ve ordu müziði çalgýsý olarak tasarlanmýþtý. Saksofon, 1840’larýn baþýnda Paris’te yaþayan Belçika’lý müzik aygýtlarý yapýmcýsý ve klarnetçi Antoine - Joseph Adolphe Sax tarafýndan tasarlandý. ‘’Saksofon’’ adý da “sax’ýn sesi” anlamýný taþýr. Sax’ýn 1846’da patentini aldýðý konusunda deðiþik görüþler öne sürülse de, en olasý olaný, ophicleide çalgýsýna klarnet aðýzlýðý eklenmesiyle ortaya çýktýðýdýr.Gerçekten de sax, babasýnýn klarnet ve ophicleide üretilen fabrikasýnda yýllarca çalýþmýþtýr. Sax’ýn 1846’da aldýðý patentten sonraki yirmi yýl boyunca, saksofon yalnýzca Sax’ýn fabrikasýnca üretildi. 1866’de, patent süresinin bitiminden sonra saksofonda öteki üreticilerce birçok deðiþiklik yapýldý. Saksofon klarnete benzer.Klarnetteki gibi, içi yuvarlak ya da dört köþe oyulmuþ, tek kamýþlý aðýzlýk kullanýr.Tuþ sistemi ise ayný olmasa da flütle benzerlik gösterir.Saksofon, metalden yapýlan bir enstrüman olmasýna raðmen, yapýsý ve kökeni gereði, bakýr üflemeli deðil, tahta üflemeli çalgýlar arasýnda sayýlýr. "));
        explanations.Add(AciklamaConventer(31, "ARMONÝKA", "Nefesli", "Armonika ya da mýzýka, üflemeli bir çalgýdýr. Ýtalyanca armonica kelimesinden türemiþtir. Nefes ve dil ile çalýnan delikli bir çalgýdýr. Ýçinde borucuklara yerleþtirilmiþ bir dizi serbest dilin bulunduðu delikli bir kutudur. Kutu nefes vererek ve alarak aðýz hizasýnda gezdirilince ses çýkarýr. Blues, Country ve Western gibi müzik dallarýnda oldukça yaygýndýr."));
        explanations.Add(AciklamaConventer(32, "FLÜT", "Nefesli", "Flüt nefesli grubundaki müzik aletileri ailesidir. Kamýþlý nefesli çalgýlarýn aksine flüt sesini bir açýklýk boyunca hava akýþýndan çýkaran bir aerofon veya kamýþsýz nefesli çalgýdýr. Hornbostel – Sachs alet sýnýflandýrmasýna göre oluklar kenardan üflemeli aerofonlar olarak sýnýflandýrýlýr.[1] Flüt çalan bir müzisyen flütçü olarak adlandýrýlýr. Flütler bilinen en eski, tanýmlanabilir müzik aletleridir çünkü elle delinmiþ deliklere sahip paleolitik örnekler bulunmuþtur. Günümüzün Almanya Swabian Jura bölgesinde yaklaþýk 43,000 ila 35,000 yýl öncesine ait bir dizi flüt bulundu. Bu flütler Avrupada modern insan varlýðýnýn en erken döneminden itibaren geliþmiþ bir müzik geleneðinin var olduðunu göstermektedir.Þu anda bilinen en eski flütler Avrupada bulunurken Asyanýn da günümüze kadar devam eden enstrümanla uzun bir geçmiþi vardýr.Çinde yaklaþýk 9000 yýllýk çalýnabilir bir kemik flüt keþfedildi.[4] Amerika kýtasý da Caral, Peru da bulunan, 5000 yýl öncesine ve Labrador da bulunan yaklaþýk 7500 yýl öncesine dayanan eski bir flüt kültürüne sahipti.  "));
        explanations.Add(AciklamaConventer(33, "MELODÝKA", "Nefesli", "Melodika, üflemeli ve tuþlu bir müzik enstrümanýdýr. Bu enstrüman aðýzla üflenir ve tuþlara basýlarak ses çýkarýlýr. Flüte benzer ama tek bir tuþa basýnca o nota çýkar. Baþlangýþ notasý fa ve bitiþ notasý do dur . Parmaklar numaralandýrýlýr baþ parmak 1 iþaret parmaðý 2 orta parmak 3 yüzük parmaðý 4 serçe parmaðý da 5 olarak adlandýrýlýr. Yeni baþlayanlar uzun hortumuyla kullanýrlar.Sol elle tutulur, sað elle üzerindeki(piyano tuþlarý gibi) tuþlarýna basýlarak çalýnýr.Arkasýnda tutacak sapý bulunur. Müzik eðitimi için ideal bir çalgýdýr. Melodika genelde ilköðretim okullarýnda eðitim amaçlý olarak müzik derslerinde çalýnmaktadýr. Diatonik ve kromatik aralýklardan oluþur.Notasý ikinci çizgi Sol açkýsý ile yazýlýr.Çalgý sol elle tutulup, üfleme deliðinden soluk verilirken, sað el parmaklarý dokunaklar üzerinde dolaþarak ezgi seslendirilir. Çocuklarýn müzik eðitiminde ve geliþtirilmesinde önemli bir yeri olduðu söylenebilir. Daha çok ilkokul müzik eðitiminde önem kazanmýþ bir çalgýdýr. "));
        explanations.Add(AciklamaConventer(34, "VÝYOLA", "Yaylý", "Viyola, yaylý çalgýlar ailesinin ikinci üyesidir ve þekli kemana benzemekle beraber kemandan biraz daha büyüktür. Alto da denir. Fiziki yapýsý kemana çok benzer. Keman ailesi müzik aletlerinde orta sese sahiptir ve keman ile çello arasýnda yer alýr. Gövde uzunluðu 41 – 45 cm civarýndadýr. Notalarý üçüncü çizgi ‘do’ anahtarýyla yazýlýr. Ýnce seslerde ‘sol’ anahtarý kullanýlýr. Viyolanýn da dört teli vardýr ve tam beþli aralýklarla DO, SOL, RE, LA olarak akort edilir. Viyola teknik açýdan da kemana çok benzer.Parmak ve yay tekniði, pozisyonlar ve deðiþik ses renklerini elde etme yöntemleri kemandan farksýzdýr.Fakat viyolanýn genelde koyu, derin ve can alýcý bir ses rengi vardýr. Tellerin her biri kendine özgü bir kiþilik taþýr. La telinden elde edilen sesler, tatlý ve ýlýmlý duyulur. Re telinin gösteriþsiz ama yumuþak bir rengi vardýr. Sol teli çok zengin týnýlar üretir, en alttaki Do teli, özgün ses rengiyle, ciddi ve egemen bir yol göstericidir. Hoþgörüyü ve güven veren duyarlýlýðý anýmsatýr. Derinden gelen alto sesiyle viyola acýyý, hüznü ve sevdayý anlatmakta etkilidir. Kimi zaman sertliðe varabilen ince seslerle, ürkütücü çýðlýklarý duyururken, kimi zaman tatlý bir öðütçülüðe yönelir. Bu özellikleri içeren viyolaya, orkestrada armoni eþliðinin orta partilerini seslendirme görevi verilir.Çünkü viyolanýn ses alaný, orkestranýn ses alanýnýn tam ortasýndadýr. Kimi zaman özelliklerinden faydalanmak için bu çalgýya karakteristik ezgileri seslendirme görevi de verilir. "));
        explanations.Add(AciklamaConventer(35, "KONTRBAS", "Yaylý", "Kontrbas, keman ailesinden, yaylýlar grubunun en kalýn sesli çalgýsýdýr. 4 telli ve 5 telli olmak üzere iki türü bulunur. 5 telli olan kontrbasa senfonik orkestra eserlerinin icrasýnda sýk olarak ihtiyaç duyulmaktadýr. Kontrbasýn, özel bir tür çelikten yapýlmýþ dört teli vardýr. Bunun yanýnda yalnýz baðýrsak ya da baðýrsak üzerine çelik sargý veya baþka yapay malzemeler kullanýlarak hazýrlanmýþ teller de bulunabilmektedir. Yay kullanýlarak ya da parmaklar yardýmýyla çalýnýr. Diðer yaylý çalgýlardan farklý olarak iki çeþit yay modeli bulunmaktadýr. Bunlar Alman ve Fransýz olarak adlandýrýlmaktadýr. Alman modelde yay yan taraftan kavranarak tutulurken, Fransýz modelde yay yukarýdan tutulur. Ayrýca senfonik orkestralarýn, caz, pop ve rock müzik topluluklarýnýn ana çalgýlarýndan biridir. Kontrbas, özellikle caz müziðin vazgeçilmez çalgýsý olmuþtur. Kontrbas Violone adý verilen bir Rönesans çalgýsýndan türemiþtir. 16. yüzyýldan 18. yüzyýla kadar bir deðiþim süreci geçirmiþtir. 18. yüzyýlýn ikinci yarýsýnda bugünkü biçimini almýþtýr. Çok büyütülmüþ bir keman görünümündedir. Boyu 1,80 metre, eni 60 cm olan kontrbas bu boyutlarý nedeniyle ayakta ya da yüksek bir tabureye oturularak çalýnýr."));
        explanations.Add(AciklamaConventer(36, "VÝYOLONSEL", "Yaylý", "Viyolonsel ya da çello yaylý çalgýlarýn bir türüdür. Viyolonsel, yaylý ailesinden dört telli ve tenor sesli bir çalgýdýr. Bu çalgýnýn atasý viola da gamba adý verilen 4 adet teli bulunan perdeli bir çalgýdýr. Viyolonsel; keman, viyola ve kontrbas ile ayný ailedendir. Keman ile viyolonselin þekilleri büyük oranda birbirini andýrsa da boyutlarý çok farklýdýr. 16.yüzyýlda ilk örnekleri Fransada ortaya çýkan bu çalgýnýn þekli kadýn vücudunu andýrýr. Baþlangýçta beþ telli olarak yapýlan bu çalgý, önceleri orkestrada bas sesleri desteklemek için kullanýlmýþtýr. Tek baþýna belirgin bir çalgý olarak ortaya çýkmasý ise 18. yüzyýlda olmuþtur. Viyolonsel genellikle akustik olarak kullanýlan bir enstrümandýr.Her mekânda(açýk alan, konser salonu vs.) çalýnabilir.Karþýdan bakýldýðýnda gövdesinin orta bölümünde bulunan ve el yazýsý ile f harfini andýran 2 ses deliði ve eþik(köprü) olarak adlandýrýlan bir destek bulunur.Yayýn tellere temasý ile titreþen tellerden çýkan ses, eþik tarafýndan gövdenin içindeki havayý titreþtirerek, içeride dikey olarak bulunan can direðine iletilir ve bu deliklerden geri döner. "));
        explanations.Add(AciklamaConventer(37, "OBUA", "Nefesli", "Obua, nefesli çalgýlar ailesinden bir müzik aletidir. 1170 yýlýndan önce hautbois denilen obuanýn sözcük kökeni Fransýzcadan Ýngilizceye geçen HAUT (yüksek) ve BOIS (ahþap nefesli çalgý) bileþik kelimesinden türetilmiþtir. Obua, aðýz ve hava basýncýyla çalýnýr. Ahþaptan yapýlan Obuanýn üretiminde genellikle abanoz ya da gül aðacý tercih edilmekle birlikte þimþir ve sedir aðaçlarý da kullanýlmaktadýr. Obua, 17.yüzyýlda iki Fransýz müzisyeni, Jean Hotteterre ve Michel Philidor tarafýndan icat edilmiþtir. “Shawm” adý verilen bir enstrümaný “Hautbois” (obua)ya çevirdiler. “Hautbois” in Shawm’dan daha dar ve 3 parçalý bir gövdesi vardý. 18.yüzyýlda çoðu orkesra bu enstrümaný bünyesine katmaya baþlamýþtý. Tahta dýþýnda ebonitten üretilen modelleri de bulunan obua, üstten alta doðru geniþleyen konik bir biçeme sahiptir. üst alt ve gövdelerle kalak denen orta bölüm olarak üç parçadan oluþur. En üstte bulunan ince ve küçük metal (gümüþ ya da nikel) boruya takýlan çift kamýþlý bir aðýzlýðýn üflenmesiyle çalýnýr. Bu aðýzlýk kamýþtan iki parçanýn balmumlu ince iple baðlanmasýyla birleþtirilmiþtir. Profesyonel obuacýlar kendi aðýzlýklarýný kendileri hazýrlamaktadýr. "));
        explanations.Add(AciklamaConventer(38, "TROMBET", "Nefesli", "Trompet, bir aðýzlýk ve kendi üstüne kývrýlmýþ silindir bir borudan oluþan nefesli çalgýdýr. Bir nefesli çalgý, askeri borazan < Ýt tromba boru +ette[2]) Aerophone sýnýfýna ait bir bakýr üflemelidir. Bakýr ailesi enstrümanlarýnýn 3 temel bölümü vardýr: Aðýzlýk, boru, ve kalak, günümüz pistonlu bakýrlarýnda piston kýsmý, boru kýsmýnýn üzerindedir. Ayrýca aðýzlýðý, boruya baðlayan kýsma ‘backbore’ ya da aðýzlýk borusu denir. M.Ö. 1480 yýllarýnda yapýldýðý düþünülen, Mýsýr - Deir el - Bahri’de bulunan Kraliçe Hatþepsut tapýnaðýndaki ikonografilerle trompetin bir çalgý biçiminde kullanýldýðý belgelenmiþtir. Bu ikonografilerde, bir festivalde trompet çalan Mýsýrlý askerler görülmektedir. Firavun Tutankamon’un mezarýnda ise iþlemeli trompetler bulunmuþtur. Tutankamon’un M.Ö. 1480 tarihlerinde yaþadýðý sanýlmaktadýr.Gerek ikonografilerde çizilen gerek mezarda bulunan trompetler, insanoðlunun önceden boynuzdan yansýttýðý soluðunu bu kez kendi yaptýðý bir boruya bir aðýzlýk takýp aktararak müzik üretme yolunda önemli bir aþamaya ulaþtýðýný göstermektedir.Aðýzlýk ve kalak bölümlerinden oluþan bu basit trompetler temel bir sesin armonik doðuþkanlarýný çýkarýyordu. "));
        explanations.Add(AciklamaConventer(39, "TROMBON", "Nefesli", "Trombon, üflemeli ve bakýr nefesli çalgýlar grubunda bulunan bir çalgý çeþididir. Fincan biçimli bir aðýzlýða dayanan dudaklarýn titreþmesiyle ses çýkarmaktadýr.Boru uzunluðunu deðiþtiren ve kulis adý verilen bir sürgüsü vardýr.Bu sürgü trombonun farklý notalarda ses çýkarabilmesini saðlar. Trombonun kulis adý verilen bu parçasý; boru uzunluðu arttýkça pesleþir, kýsaldýkça tizleþir.Bu sayede bu enstrüman kolay bir þekilde Glissando denilen tekniði yapabilir.Trombon, ilk olarak 15.yüzyýlda kullanýlmýþtýr. "));
        explanations.Add(AciklamaConventer(40, "KORNO", "Nefesli", "Korno, salyangoz kabuðu gibi kývrýmlý, bakýr borudan yapýlan, üflemeli bir çalgýdýr. Ýtalyancada boynuz anlamýna gelen corno sözcüðünden Türkçeye geçmiþtir. Antik Mýsýrda, Antik Romada ve Mezopotamyada boynuzdan yapýlan ilk örnekleri, iþaret vermek ve avcýlara yol göstermek için kullanýlýrdý. Günümüzde bazý ülkelerde çobanlar ve sürek avlarýnda avcýlar hala bu amaçla boynuz kullanýrlar. Kornonun gövdesini oluþturan boru, üflenen baþ bölümden alt uca doðru kývrýlarak geniþler ve çan biçimli kalak bölümüyle son bulur. Çaðdaþ kornonun boru uzunluðu yaklaþýk 4,5 metredir.Üflenen ucunda koni biçiminde bir aðýzlýk vardýr.Üzerinde flüt ya da klarnette olduðu gibi ses delikleri ya da üfleme dili yoktur. Dudaklar, yapýsýna uygun olarak seçilen aðýzlýða üçte ikisi üst dudaða, üçte biri alt dudaða gelecek þekilde ve dudaklarý yatay olarak ortalayacak biçimde yerleþtirilir.Aðýzlýða üflendiðinde ilk önce dudaklarda buzz adý verilen arý výzýltýsý gibi bir ses elde edilir, bu ses borularýn içerisinden geçer, çeþitli titreþimlerden sonra enstrümanýn ses çýkýþ yeri olan Kalaktan yükseltilmiþ olarak duyulur. Çýkan sesler üfleme sýrasýnda dudaklarýn duruþ biçimine göre deðiþir. Dudaklar gevþek býrakýlýrsa bas sesler, gergin tutulursa tiz sesler elde edilir. "));
        explanations.Add(AciklamaConventer(41, "BANDONEON", "Tuþlu", "Bandoneon, tahminen 1845 yýlýnda icat edilen ve Almanya kökenli tuþlu ve körüklü koncertinas isimli enstrümanlar ailesinin bir üyesidir. Koncertinas –Ýngiliz Adalarý’nda kullanýlanlardan farklý olarak- her iki tarafýnda 14 tuþu olan küçük kare bir enstrümandýr. Tahminen 1856 yýlýnda tuþ sayýsý 70’ten fazlaya çýkarýlmýþ ve bu enstrümana Krefeld, kuzey Almanya’da bir müzik dükkâný sahibi Heinrich Band anýsýna ticari isim olarak ‘Bandoneon’ adý verilmiþtir. Bandoneon Buenos Aires’e çok çabuk uyum saðladý ve Tango’nun sembolü oldu.Hiçbir zaman BsAs’de üretilmedi. 1911’den savaþ sonrasý birkaç yýla kadar çoðu bandoneon Alman Alfred Arnold tarafýndan yapýlmýþtýr.Bandoneon dünyasýnda AA(Double A - Dople A) olarak anýlan Arjantin bandoneonu 71 tuþlu ve 2 sesli bir enstrümandýr.Her tuþ körüðün açýlma veya kapanma pozisyonuna göre farklý nota çalar.Dünyada ve özellikle Arjantinde kabul gören bisonorik sistemdir.Sað elde tiz sesler ve sol elde de bas seslerin bulunduðu bandoneon, açarken ve kaparken ayný tuþlardan faklý sesler çýkardýðý için aslen 4 farklý klavyeye sahiptir.Bu yüzden dünyanýn en zor enstrumanlarý arasýnda gösterilmektedir. "));
